// builds search
$(document).ready(function() {
  $('input#search').on('keyup', function () {
    var resultdiv = $('#results');
    // Get query
    var query = $(this).val();
    // Search for it
    var result = index.search(query);
    // Show results
    resultdiv.empty();
    // Add status
    resultdiv.prepend('<p class="search-result-message">Ergebnisse: '+result.length+'</p>');
    // Loop through, match, and add results
    for (var item in result) {
      var ref = result[item].ref;
      var tags = store[ref].tags;

      var tagsHtml = "";
      tags.forEach(function (item, index) {
          var linkName = item.replace(/ /g, "-").toLowerCase();
          var teamName = item.replace("ue", "ü").replace("oe", "ö").replace("ae", "ä");
          tagsHtml += '<a href="/verein/' + linkName +'" class="searchhit-meta-team">' +teamName+ '</a>';
      });

      var searchitem = '<div class="searchhit"><a href="'+store[ref].link+'" class="searchhit-title">'+store[ref].title+'</a><div class="searchhit-excerpt">'+store[ref].excerpt+'</div><div class="searchhit-meta">'+tagsHtml+'</div>';
      resultdiv.append(searchitem);
    }
  });
});
